@use 'functions' as *;

$breakpoints-up: (
  'medium': em(768),
  'large': em(1024),
  'xlarge': em(1440),
);

$breakpoints-down: (
  'medium': em(767.98),
  'large': em(1023.98),
  'xlarge': em(1439.98),
);

@mixin breakpoint($size) {
  @media (min-width: map-get($breakpoints-up, $size)) {
    @content;
  }
}

@mixin breakpoint-down($size) {
  @media (max-width: map-get($breakpoints-down, $size)) {
    @content;
  }
}

