@use '../util' as *;

.testimonials {
  text-align: center;
  padding: rem(64) rem(24) rem(90);

  @include breakpoint(large) {
    padding-block: rem(150);
  }

  &__container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: rem(64) rem(40);
    margin-block-start: rem(64);

    @include breakpoint(large) {
      flex-direction: row;
      justify-content: center;
      margin-block-start: rem(82);
    }
  }

  &__content {
    max-width: rem(430);
    display: flex;
    flex-direction: column;
    align-items: center;

    @include breakpoint(large) {
      max-width: rem(350);
    }
  }

  &__avatar {
    width: rem(72);
    height: rem(72);
    border-radius: 50%;
  }

  &__comment {
    padding-block: rem(38);

    @include breakpoint(large) {
      padding-block: rem(64) rem(74);
    }
  }

  &__author {
    display: flex;
    flex-direction: column;
    row-gap: rem(14);
    color: $clr-very-dark-desaturated-blue;
  }
}

