@use '../util' as *;

.projects {
  display: grid;
  grid-template-columns: repeat(2, 1fr);

  @include breakpoint(medium) {
    grid-template-columns: repeat(4, 1fr);
  }

  &__picture {
    max-height: rem(300);

    @include breakpoint(medium) {
      max-height: none;
    }

    @include breakpoint(large) {
      max-height: rem(800);
    }

    img {
      object-position: center 80%;
    }
  }
}

sources.scss\\layout\\_footer.scss:
@use '../util' as *;

.footer {
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: $clr-ligh-cyan;
  color: $clr-dark-cyan;
  padding: rem(64) rem(24) rem(16);

  &__logo {
    color: inherit;

    svg {
      width: rem(170);
      height: rem(34);
    }

    path {
      fill: currentColor;
    }
  }

  &__menu {
    width: min(100%, rem(300));
    margin-block: rem(40) rem(90);
  }

  &__list {
    display: flex;
    justify-content: space-between;
    column-gap: rem(24);
  }

  &__link {
    color: $clr-dark-moderate-cyan;
    transition: color 150ms;

    &:hover,
    &:focus-visible {
      color: $clr-white;

      path {
        fill: currentColor;
      }
    }
  }

  &__socials {
    display: flex;
    align-items: center;
    column-gap: rem(28);
  }

  &__attribution {
    text-align: center;
    padding-block-start: rem(28);
  }
}
